import { getItems, getUrgentItems } from '../actions/item';
import ItemCard from '../components/item/ItemCard';
import { getCookie } from '../actions/auth';
import { useState, useEffect } from 'react';
import Search from './item/Search';
import item from '../../backend/models/item';

const Main = () => {

    const [items, setItems] = useState([]);
    const [urgentItems, setUrgentItems] = useState([]);
    const [searchedItems, setSearchedItems] = useState([]);
    const [sortUrgent, setSortUrgent] = useState(false);
    const [sortSearched, setSortSearched] = useState(false);

    useEffect(() => {
        initItems();
    }, []);

    const initItems = () => {
        const token = getCookie('token');
        getItems(token).then(data => {
            if(data.error) {
                console.log(data.error);
            } else {
                setItems(data);
            }
        });
        getUrgentItems(token).then(data => {
            if(data.error) {
                console.log(data.error)
            } else {
                setUrgentItems(data);
            }
        });
    };


    const showItems = () => {
        if(sortUrgent) {
            return urgentItems.map((item) => {
                return (
                    <article key={item._id}>
                        <ItemCard item={item} updateParent={updateUrgentItemsState}/>
                    </article>
                );
            });
        } else if (sortSearched) {
            return searchedItems.map((item) => {
                return (
                    <article key={item._id}>
                        <ItemCard item={item} />
                    </article>
                );
            });
        } else {
            return items.map((item) => {
                return (
                    <article key={item._id}>
                        <ItemCard item={item} updateParent={updateItemsState}/>
                    </article>
                );
            });
        }
    };

    const handleUrgentToggle = () => {
        setSortUrgent(document.getElementById("sort-urgent").checked);
    };

    const updateItemsState = item => {
        const i = items.indexOf(item);
        setItems(items.filter((current, index) => {
            if (index === i) {
                return false;
            } else {
                return true;
            }
        }));
    };

    const updateUrgentItemsState = item => {
        const i = urgentItems.indexOf(item);
        setUrgentItems(urgentItems.filter((current, index) => {
            if (index === i) {
                return false;
            } else {
                return true;
            }
        }));
    };

    const updateSearchedItemsState = (returnedItems, status) => {
        setSortSearched(status);
        setSearchedItems(returnedItems);
    };

    return (
        <div className='main'>
            <div className='main__title'>
                <div>
                    <div className='text-center'>
                        <h2>Shopping List</h2>
                    </div>
                    <div className='row main__searchBar'>
                        <div>
                            <Search updateParent={updateSearchedItemsState}/>
                        </div>
                        <div className='ml-3'>
                            <label>
                                <input id='sort-urgent' type='checkbox' onChange={handleUrgentToggle}/>
                                <span>Sort Urgent</span>
                            </label>
                        </div>
                    </div>
                </div>
                <hr />
            </div>
            <div className='main__body'>
                {showItems()}
            </div>
        </div>
    );
};


export default Main;
