import moment from 'moment';
import { updateItem, removeItem } from '../../actions/item';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import CreateIcon from '@material-ui/icons/Create';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import Checkbox from 'rc-checkbox';
import { IconButton } from '@material-ui/core';
import { useEffect, useState } from 'react';
import { getCookie } from '../../actions/auth';

const ItemCard = ({ item, updateParent }) => {
    const [token, setToken] = useState('');
    const [values, setValues] = useState({
        name: '',
        amount: 0,
        urgent: false,
        note: '',
        error: false,
        success: false,
        loading: false
    });

    const { name, amount, urgent, note, error, success, loading } = values;

    const init = () => {
        setToken(getCookie('token'));
        setValues({
            ...values,
            name: item.name,
            amount: item.amount,
            urgent: item.urgent,
            note: item.note,
        });
    };

    useEffect(() => {
        init();
    }, []);

    const plusAmount = () => {
        setValues({
            ...values,
            amount: amount + 1
        });
        updateItem({
            slug: item.slug,
            amount: amount + 1
        }, token);
    };

    const minusAmount = () => {
        if (amount === 1) {
            removeItem({
                slug: item.slug
            }, token);
            updateParent(item, 'remove');
        } else {
            setValues({
                ...values,
                amount: amount - 1
            });
            updateItem({
                slug: item.slug,
                amount: amount - 1
            }, token);
        } 
    };

    const toggleUrgent = () => { 
        setValues({
            ...values,
            urgent: !urgent
        });
        updateItem({
            slug: item.slug,
            urgent: !urgent
         }, token);
        updateParent(item, 'urgent');
    };

    const updateNote = () => { 
        let newNote = prompt('Update Note:', note);
        if (newNote.length > 40) {
            newNote = newNote.slice(0, 40)
        }
        setValues({
            ...values,
            note: newNote
        });
        updateItem({
            slug: item.slug,
            note: newNote
        }, token);
    };

    const boughtItem = () => {
        removeItem({
            slug: item.slug
        }, token);
        updateParent(item, 'remove');
    };

    
    return (
        <div className='lead pb-2 main__card'>
            <header className='main__card__title'>
                <div className='row' style={{display: 'flex', verticalAlign: 'center'}}>
                    <div className='pl-3' onClick={boughtItem}>
                        <IconButton>
                            <CheckCircleIcon />
                        </IconButton>
                    </div>
                    <div className=''>
                        <h3 className='pt-1 pl-1 pb-2 mb-0 font-weight-bold'>{name} |</h3>
                    </div>
                    <div className='pt-1'>
                        <p className='pt-2 mb-0 pl-2'>
                            Added by {item.postedBy} {moment(item.createdAt).fromNow()} |
                        </p>
                    </div>
                    <div className='pl-2' style={{display: 'grid', placeItems: 'center'}}>
                        <p className='mb-0 pr-2'>Amount: {amount} |</p>
                    </div>
                    {urgent && (
                        <div style={{display: "flex", alignItems: 'center', paddingTop: '7px', paddingLeft: '3px'}}>
                            <label>
                                <Checkbox defaultChecked onChange={toggleUrgent} />
                                <span className='pl-1'>Urgent |</span>
                            </label>
                        </div>
                    )}
                    {!urgent && (
                        <div style={{display: "flex", alignItems: 'center', paddingTop: '7px'}}>
                            <label>
                                <Checkbox onChange={toggleUrgent} />
                                <span className='pl-1'>Urgent |</span>
                            </label>
                        </div>
                    )}
                    <div className='row pl-3' style={{position: 'relative', left: '30px'}}> 
                       <div onClick={plusAmount}>
                            <IconButton>
                                <AddIcon />
                            </IconButton>
                       </div>
                       <div onClick={minusAmount}>
                            <IconButton >
                                <RemoveIcon />
                            </IconButton>
                       </div>
                    </div>
                </div>
            </header>
            <div className='main__card__info pl-1'>
                <section>
                    <div className='row pl-3'>
                        <div style={{display: 'grid', placeContent: 'center'}}>
                            <div className=''>
                                <p style={{marginBottom: '0px'}}>Note: {note}</p>
                            </div>
                        </div>
                        <div>
                            <div className='pl-2' onClick={updateNote}>
                                <IconButton>
                                    <CreateIcon />
                                </IconButton>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
            <hr className='mb-0 mt-2'/>
      </div>
    );
};

export default ItemCard;
