import { getItems } from '../actions/item';
import ItemCard from '../components/item/ItemCard';
import { getCookie, isAuth } from '../actions/auth';
import { useState, useEffect } from 'react';
import cookie from 'js-cookie';

const Main = () => {

    const [items, setItems] = useState([]);
    const [token, setToken] = useState('');

    useEffect(() => {
        initItems();
        initToken();
    }, []);


    const initToken = () => {
        setToken(getCookie('token'));
    };

    const initItems = () => {
        const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZjU4ZGYwZjU0ZDgyZTMyMWM5ZjdhMjAiLCJpYXQiOjE1OTk4MjgwNTksImV4cCI6MTU5OTkxNDQ1OX0.TrNt-CoMydszew6LFvKAav9WqDtNHQm1kuSLOqFJdKY';
        console.log('initItems()', getCookie('token'))
        getItems(token).then(data => {
            if(data.error) {
                console.log(data.error);
            } else {
                setItems(data);
            }
        });
    };


    const showListedItems = () => {
        console.log(getCookie('token'), '*******************', items.length);
        return items.map((item, i) => {
            return (
                <article key={i}>
                    <ItemCard item={item}/>
                </article>
            );
        });
    };
  

    return (
        <div className='main'>
            <div className='main__title'>
                <h2>Shopping List</h2>
                <hr />
            </div>
            <div className='main__body'>
                {showListedItems()}
            </div>
        </div>
    );
};


export default Main;
