import moment from 'moment';
import { updateItem, removeItem } from '../../actions/item';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import CreateIcon from '@material-ui/icons/Create';
import PriorityHighIcon from '@material-ui/icons/PriorityHigh';
import LowPriorityIcon from '@material-ui/icons/LowPriority';

import DeleteIcon from '@material-ui/icons/Delete';
import Checkbox from 'rc-checkbox';
import { IconButton } from '@material-ui/core';
import { useEffect, useState } from 'react';
import { getCookie } from '../../actions/auth';

const ItemCard = ({ item, updateParent }) => {
    const [token, setToken] = useState('');
    const [values, setValues] = useState({
        name: '',
        amount: 0,
        urgent: false,
        note: '',
        error: false,
        success: false,
        loading: false
    });

    const { name, amount, urgent, note, error, success, loading } = values;

    const init = () => {
        setToken(getCookie('token'));
        setValues({
            ...values,
            name: item.name,
            amount: item.amount,
            urgent: item.urgent,
            note: item.note,
        });
    };

    useEffect(() => {
        init();
    }, []);

    const plusAmount = () => {
        setValues({
            ...values,
            amount: amount + 1
        });
        updateItem({
            slug: item.slug,
            amount: amount + 1
        }, token);
    };

    const minusAmount = () => {
        if (amount === 1) {
            removeItem({
                slug: item.slug
            }, token);
            updateParent(item, 'remove');
        } else {
            setValues({
                ...values,
                amount: amount - 1
            });
            updateItem({
                slug: item.slug,
                amount: amount - 1
            }, token);
        } 
    };

    const toggleUrgent = () => { 
        setValues({
            ...values,
            urgent: !urgent
        });
        updateItem({
            slug: item.slug,
            urgent: !urgent
         }, token);
        updateParent(item, 'urgent');
    };

    const updateNote = () => { 
        let newNote = prompt('Update Note:', note);
        if (newNote && newNote.length > 40) {
            newNote = newNote.slice(0, 40)
        }
        setValues({
            ...values,
            note: newNote
        });
        updateItem({
            slug: item.slug,
            note: newNote
        }, token);
    };

    const boughtItem = () => {
        removeItem({
            slug: item.slug
        }, token);
        updateParent(item, 'remove');
    };

    
    return (
        <div className='lead pb-3 main__card'>
            <header className='main__card__title'>
                <div className='container-fluid'>
                    <div className='row placeCenter'>
                        <div className='placeCenter row col-6 justify-content-end'>
                            <div className='col-4 pr-1' style={{display: 'grid', placeItems: 'center'}}>
                                <h3 className='mb-0 font-weight-bold'>{amount} </h3>
                            </div>
                            <div className='col-8 pl-3 justify-content-start placeCenter'>
                                <a title={`Added by ${item.postedBy} ${moment(item.createdAt).fromNow()}`}>
                                    <h3 className='pt-1 pb-0 mb-0 font-weight-bold placeCenter'>{name}</h3>
                                </a>
                            </div>
                        </div>
                        <div className='row col-6 justify-content-start'> 
                            <div onClick={plusAmount}>
                                    <IconButton>
                                        <AddIcon />
                                    </IconButton>
                            </div>
                            <div onClick={minusAmount}>
                                    <IconButton >
                                        <RemoveIcon />
                                    </IconButton>
                            </div>
                            {urgent && (
                                    <div className='main__card__urgent'>
                                        <a title='Urgent'>
                                            <label>
                                                <Checkbox defaultChecked onChange={toggleUrgent} />
                                                <span className='pl-1'>
                                                    <PriorityHighIcon style={{color: '#3dbcf6', fontSize: '15px'}}/>
                                                </span>
                                            </label>
                                        </a>
                                    </div>
                                )}
                                {!urgent && (
                                    <div className='main__card__urgent'>
                                        <a title='Not Urgent'>
                                            <label>
                                                <Checkbox onChange={toggleUrgent} />
                                                <span className='pl-1'>
                                                    <LowPriorityIcon style={{color: 'white', fontSize: '15px'}}/>
                                                </span>
                                            </label>
                                        </a>
                                    </div>
                                )}
                            <div>
                                <div className='pl-3' onClick={updateNote}>
                                    <IconButton>
                                        <CreateIcon />
                                    </IconButton>
                                </div>
                            </div>
                            <div className='pl-3' onClick={boughtItem}>
                                <IconButton>
                                    <DeleteIcon />
                                </IconButton>
                            </div>
                        </div>
                    </div>
                </div>
            </header>
            
            {note && (<div className='main__card__info pl-1'>
                <section>
                    <div className='pl-3'>
                        <div className='pl-3 pt-1 pb-1'>
                            <p style={{marginBottom: '0px', paddingLeft: '35px'}}>Note: {note}</p>
                        </div>
                    </div>
                </section>
            </div>)}
      </div>
    );
};

export default ItemCard;
