import { getItems, getUrgentItems } from '../actions/item';
import ItemCard from '../components/item/ItemCard';
import { getCookie } from '../actions/auth';
import { useState, useEffect } from 'react';
import Search from './item/Search';
import item from '../../backend/models/item';
import Checkbox from 'rc-checkbox';
import eventHub from '../helpers/eventHub';

const Main = () => {

    const [token, setToken] = useState('');
    const [items, setItems] = useState([]);
    //const [urgentItems, setUrgentItems] = useState([]);
    //const [searchedItems, setSearchedItems] = useState([]);
    const [sortUrgent, setSortUrgent] = useState(false);
    const [sortSearched, setSortSearched] = useState(false);

    let firstTime = true;
    useEffect(() => {
        initItems();
        if (firstTime) {
            eventHub.on('itemAdded', () => {
                initItems();
            });
            firstTime = false;
        }
    }, []);

    

    const initItems = () => {
        const tempToken = getCookie('token');
        getItems(tempToken).then(data => {
            if(data.error) {
                console.log(data.error);
            } else {
                setItems(data);
            }
        });
        /*getUrgentItems(tempToken).then(data => {
            if(data.error) {
                console.log(data.error)
            } else {
                setUrgentItems(data);
            }
        });*/
        setToken(tempToken);
    };


    /*const showItems = () => {
        if(sortUrgent) {
            return urgentItems.map((item) => {
                return (
                    <article key={'urgent ' + item._id}>
                        <ItemCard item={item} updateParent={updateItemsState}/>
                    </article>
                );
            });
        } else if (sortSearched) {
            return searchedItems.map((item) => {
                return (
                    <article key={'search ' +item._id}>
                        <ItemCard item={item} updateParent={updateItemsState}/>
                    </article>
                );
            });
        } else {
            return items.map((item) => {
                return (
                    <article key={item._id}>
                        <ItemCard item={item} updateParent={updateItemsState}/>
                    </article>
                );
            });
        }
    };*/

    const showItems = () => {
        return items.map((item) => {
            return (
                <article key={item._id}>
                    <ItemCard item={item} updateParent={updateItemsState}/>
                </article>
            );
        });
    };

    const handleUrgentToggle = () => {
        setSortUrgent(document.getElementById("sort-urgent").checked);
    };

    const toggleSearchSort = (returnedItems, status) => {
        setSearchedItems([...returnedItems]);
        setSortSearched(status);
    };

    const updateItemsState = (item, update) => {
        const i = items.indexOf(item);
        const j = urgentItems.indexOf(item);

        if (update === 'remove') {
            setItems(items.filter((current, index) => {
                if (current.name === item.name) {
                    console.log('items')
                    return false;
                } else {
                    return true;
                }
            }));
            if(item.urgent) {
                setUrgentItems(urgentItems.filter((current, index) => {
                    if (current.name === item.name) {
                        return false;
                    } else {
                        return true;
                    }
                }));
            }
            if (searchedItems) {
                setSearchedItems(searchedItems.filter((current, index) => {
                    if (current.name === item.name) {
                        return false;
                    } else {
                        return true;
                    }
                }));
            }
        } else if (update === 'urgent') {
            items[i].urgent = !items[i].urgent;
            item.urgent =  items[i].urgent
            if (j === -1) {
                urgentItems.push(item)
            } else {
                setUrgentItems(urgentItems.filter((current, index) => {
                    if(index === j) {
                        return false;
                    } else {
                        return true;
                    }
                }));
            }
        } 
    };

    

    return (
        <div className='main'>
            <div className='main__title'>
                <div>
                    <div className='text-center'>
                        <h2>Shopping List</h2>
                    </div>
                    <div className='row main__filters'>
                        <div className='main__filters__search'>
                            <Search updateParent={toggleSearchSort}/>
                        </div>
                        <div className='ml-3 pt-2'>
                            <label style={{display: "flex", placeContent: 'center', placeItems: 'center'}}>
                                <Checkbox id='sort-urgent' onChange={handleUrgentToggle}/>
                                <span className='pl-1 pb-1'>Sort Urgent</span>
                            </label>
                        </div>
                    </div>
                </div>
            </div>
            <hr />
            <div className='main__body'>
                {showItems()}
            </div>
        </div>
    );
};


export default Main;
