import Link from 'next/link';
import renderHTML from 'react-render-html';
import { useState, useEffect } from 'react';
import { listSearch } from '../../actions/item';
import { getCookie } from '../../actions/auth';
import SearchIcon from '@material-ui/icons/Search';
import { IconButton } from '@material-ui/core';

const Search = () => {
    const [token, setToken] = useState('');
    const [values, setValues] = useState({
        search: undefined,
        results: [],
        searched: false,
        message: ''
    });

    const { search, results, searched, message } = values;

    useEffect(() => {
        init();
    }, []);

    const init = () => {
        setToken(getCookie('token'))
    };

    const searchSubmit = e => {
        e.preventDefault();
        listSearch({ search }, token).then(data => {
            if(data.length === 1){
                setValues({ ...values, results: data, searched: true, message: `${data.length} items found` });
            } else {
                setValues({ ...values, results: data, searched: true, message: `${data.length} items found` });
            } 
        });
    };

    const handleChange = e => {
        setValues({ ...values, search: e.target.value, searched: false, results: [] });
    };

    const searchedItems = (results = []) => {
        return (
            <div className="jumbotron bg-white">
                {message && <p className="pt-4 text-muted font-italic">{message}</p>}

                {results.map((item, i) => {
                    return (
                        <div key={i}>
                            <a className="text-primary">{item.name}</a>
                        </div>
                    );
                })}
            </div>
        );
    };

    const searchForm = () => (
        <form className='mt-5' onSubmit={searchSubmit}>
            <div className='row'>
                <div>
                    <input type="search" className="form-control" placeholder="Search items" onChange={handleChange} />
                </div>
                <div>
                    <IconButton>
                        <SearchIcon />
                    </IconButton>
                </div>
            </div>
        </form>
    );

    return (
        <div className="container-fluid">
            <div className="pt-3 pb-5">{searchForm()}</div>
            {searched && <div style={{ marginTop: '-120px', marginBottom: '-80px' }}>{searchedItems(results)}</div>}
        </div>
    );
};

export default Search;
