import { IconButton } from '@material-ui/core';
import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import { useState, useEffect } from 'react';
import { createItem } from '../../actions/item';
import { getCookie } from '../../actions/auth';
import Checkbox from 'rc-checkbox';
import eventHub from '../../helpers/eventHub';

const AddWeekly = () => {
    const [token, setToken] = useState('');
    const [items, setItems] = useState([]);
    const [values, setValues] = useState({
        name: '',
        amount: 1,
        note: '',
        urgent: false,
        error: false,
        success: false,
        loading: false
    });

    const { name, amount, note, urgent, error, success, loading } = values;

    useEffect(() => {
        init();
    }, [])

    const init = () => {
        setToken(getCookie('token'));
    }

    const handleChange = name => e => {
        let value;
        value = e.target.value;
        if(name === 'urgent') {
            value = document.getElementById("urgent-check").checked;
        }
        setValues({ ...values, [name]: value, error: false, success: false, loading: false })
    };

    const handleSubmit = () => {
        setValues({ ...values, loading: true });
        const newItem = {
            name: name,
            amount: amount,
            urgent: urgent,
            note: note
        }
        
    };

    const addWeeklyItems = () => {
        const date = new Date();
        const hour = date.getHours();
        const day = date.getDay();

        let firstTime = true;
        if(day === 3 && hour === 17 && firstTime) {
            for (let i = 0; i < items.length; i++) {
                let item = items[i];
                createItem({
                    name: item.name,
                    amount: item.amount,
                    urgent: item.urgent,
                    note: item.note
                }, token).then(setitems(items.filter(current => {
                    if(current.name === item.name) {
                        return false;
                    } else {
                        return true;
                    }
                })));
                eventHub.trigger('itemAdded');
            }
            firstTime = false;
        } 
        if  (day === 7 && hour === 13) {
            firstTime = true;
        }
    }; 

    const showError = () => (
        <div className='alert alert-danger mt-1' style={{ display: error ? '' : 'none' }}>
            {error}
        </div>
    );

    const showSuccess = () => (
        <div className='alert alert-success mt-1' style={{ display: success ? '' : 'none' }}>
            Item added
        </div>
    );

    const showLoading = () => (
        <div className='alert alert-info mt-1' style={{ display: loading ? '' : 'none' }}>
            Loading...
        </div>
    );

    return (
        <form className='sidebar__addItem__form'>
            <div className='sidebar__addItem__input'>
                <label>
                    <span>Name:</span>
                    <input onChange={handleChange('name')} type='text' value={name} maxLength='20' className='sidebar__addItem__input__field'/>
                </label>
            </div>
            <div className='sidebar__addItem__input'>
                <label>
                    <span>Amount:</span>
                    <input onChange={handleChange('amount')} type='number' value={amount} min='1' className='sidebar__addItem__input__field'/>
                </label>
            </div>
            <div className='sidebar__addItem__input'>
                <label>
                    <span>Note:</span>
                    <input onChange={handleChange('note')} type='text' value={note} maxLength='40' className='sidebar__addItem__input__field'/>
                </label>
            </div>
            <div className='sidebar__addItem__input'>
                <label style={{display: "flex", placeContent: 'center', placeItems: 'center'}}>
                    <Checkbox id='urgent-check' checked={urgent} onChange={handleChange('urgent')} />
                    <span className='pl-1 pb-1'>Urgent</span>
                </label>
            </div>
            <div className='sidebar__addItem__input' style={{backgroundColor: 'rgba(168, 168, 168, 0.5)', borderRadius: '20px'}} onClick={handleSubmit}>
                <IconButton>
                    <AddShoppingCartIcon />
                </IconButton>
            </div>
            <div>
                {addWeeklyItems}
                {showSuccess()}
                {showError()}
                {showLoading()}
            </div>
        </form>
    );
};

export default AddWeekly;
