import Link from 'next/link';
import renderHTML from 'react-render-html';
import moment from 'moment';
import { API } from '../../config'; 
import { updateItem, getItems } from '../../actions/item';
import AddIcon from '@material-ui/icons/Add';
import IconButton from '@material-ui/core/IconButton'
import RemoveIcon from '@material-ui/icons/Remove';
import CreateIcon from '@material-ui/icons/Create';
import { useEffect, useState } from 'react';

const ItemCard = ({ item }) => {
    const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZjU4ZGYwZjU0ZDgyZTMyMWM5ZjdhMjAiLCJpYXQiOjE1OTk3NjA4NDcsImV4cCI6MTU5OTg0NzI0N30.4g7x1JfhoBSp37JTr8h90tqmMXFKPufZIRXCgQ0T5_8';
    const [values, setValues] = useState({
        name: '',
        amount: 0,
        urgent: 0,
        note: '',
        error: false,
        success: false,
        loading: false
    });

    const { name, amount, urgent, note, error, success, loading } = values;

    const init = () => {
        setValues({
            ...values,
            name: item.name,
            amount: item.amount,
            urgent: item.urgent,
            note: item.note
        });
    };

    useEffect(() => {
        init();
    }, {});

    const addAmount = () => {
        setValues({
            ...values,
            amount: amount + 1
        });
        updateItem({
            slug: item.slug,
            amount: amount,
        }, token);
    };

    const minusAmount = () => {
        setValues({
            ...values,
            amount: amount - 1
        });
        updateItem({
            slug: item.slug,
            amount: amount
        }, token);
    };

    const toggleUrgent = () => {
        if (urgent === 0) {
            setValues({
                ...values,
                urgent: 1
            });
        } else if (urgent === 1) {
            setValues({
                ...values,
                urgent: 0
            });
        }

        updateItem({
            slug: item.slug,
            urgent: urgent
        }, token);
    };

    
    return (
        <div className='lead pb-2 main__card'>
            <header className='main__card__title'>
                <h2 className='pt-1 pl-1 pb-2 mb-0 font-weight-bold'>{name}</h2>
            </header>
            <div className='main__card__info pl-1'>
                <section>
                    <p className=' pt-2 pb-2'>
                        Added by {item.postedBy} {moment(item.createdAt).fromNow()}
                    </p>
                </section>
                <section>
                    <div className='row pl-3'> 
                        <div>
                            <p>Amount: {amount}</p>
                        </div>
                       <div>
                            <IconButton >
                                <AddIcon />
                            </IconButton>
                       </div>
                       <div>
                            <IconButton >
                                <RemoveIcon />
                            </IconButton>
                       </div>
                    </div>
                </section>
                <section>
                    {urgent === 1 && (
                        <div>
                            <label>
                                <input type='checkbox' checked='true' onChange={toggleUrgent()}/>
                                <span>Urgent</span>
                            </label>
                        </div>
                    )}
                    {urgent === 0 && (
                        <div>
                            <label>
                                <input type='checkbox' onChange={toggleUrgent()}/>
                                <span>Urgent</span>
                            </label>
                        </div>
                    )}
                </section>
                <section>
                    <div className='row pl-3'>
                        <div>
                            <div className='pt-2'>
                                <p>
                                    Note: {note}
                                </p>
                            </div>
                        </div>
                        <div>
                            <div className='pl-2'>
                                <IconButton>
                                    <CreateIcon />
                                </IconButton>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
            <hr />
      </div>
    );
};

export default ItemCard;
