import Head from 'next/head';
import Link from 'next/link';
import Layout from '../../components/Layout';
import { userPublicProfile } from '../../actions/user';
import { API, DOMAIN, APP_NAME } from '../../config';
import moment from 'moment';
//import ContactForm from '../../components/form/ContactForm';
import { isAuth } from '../../actions/auth';

const UserProfile = ({ user, query }) => {
    const head = () => (
        <Head>
            <title>
                {user.username} | {APP_NAME}
            </title>
        </Head>
    );

    return (
        <React.Fragment>
            {head()}
            <Layout>
                <div className='container'>
                    <div className='row'>
                        <div className='col-md-12'>
                            <div className='card'>
                                <div className='card-body'>
                                   <div className='row'>
                                       <div className='col-md-8'>
                                            <h5>{user.name}</h5>
                                            {user.role === 0 && (
                                                <p className='text-muted pb-3'>Joined {moment(user.createdAt).fromNow()}</p>
                                            )}
                                            {user.role === 1 && (
                                                    <p className='text-muted pb-3'>Joined {moment(user.createdAt).fromNow()} | <span className='pl-1'>Admin</span></p>
                                            )}
                                            <div>
                                                <Link href={`/user/update`}>
                                                    <a className='btn btn-primary'>Update Your Profile</a>
                                                </Link> 
                                            </div>
                                       </div>
                                   </div> 
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <br />

                <div className='container pb-5'>
                    <div className='row'>
                        <div className='col-md-6'>
                            <div className='card'>
                                <div className='card-body'>
                                    <h5 className='card-title bg-primary pt-4 pb-4 pl-4 pr-4 text-white'>
                                        Message {user.name}
                                    </h5>
                                    <br />
                                    {/*<ContactForm authorEmail={user.email}/>*/}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </Layout>
        </React.Fragment>
    );
};

UserProfile.getInitialProps = ({ query }) => {
    return userPublicProfile(query.username).then(data => {
        if (data.error) {
            console.log(data.error);
        } else {
            return { user: data.user, blogs: data.blogs, query };
        }
    });
};

export default UserProfile;
