import Link from 'next/link';
import renderHTML from 'react-render-html';
import moment from 'moment';
import { API } from '../../config'; 
import { updateItem } from '../../actions/item';
import AddIcon from '@material-ui/icons/Add';
import IconButton from '@material-ui/core/IconButton'
import RemoveIcon from '@material-ui/icons/Remove';
import CreateIcon from '@material-ui/icons/Create';

const ItemCard = ({ item }) => {
    
    return (
        <div className='lead pb-2 main__card'>
            <header className='main__card__title'>
                <h2 className='pt-1 pl-1 pb-1 font-weight-bold'>{item.name}</h2>
            </header>
            <div className='main__card__info pl-1'>
                <section>
                    <p className=' pt-2 pb-2'>
                        Added by {item.postedBy} {moment(item.createdAt).fromNow()}
                    </p>
                </section>
                <section>
                    <div className='row pl-3'> 
                        <div>
                            <p>Amount: {item.amount}</p>
                        </div>
                       <div>
                            <IconButton>
                                <AddIcon />
                            </IconButton>
                       </div>
                       <div>
                            <IconButton>
                                <RemoveIcon />
                            </IconButton>
                       </div>
                    </div>
                </section>
                <section>
                    {item.urgent === 1 && (
                        <div>
                            <input type='checkbox' id='urgent-box'/>
                            <label for='urgent-box'>Urgent</label>
                        </div>
                    )}
                    {item.urgent === 0 && (
                        <div>
                            <input type='checkbox' id='urgent-box'/>
                            <label for='urgent-box'>Urgent</label>
                        </div>
                    )}
                </section>
                <section>
                    <dev className='row pl-3'>
                        <dev>
                            {item.note && (
                                <div className='pt-2'>
                                    <p>
                                        Note: {(item.note)}
                                    </p>
                                </div>
                            )}
                        </dev>
                        <dev>
                            <IconButton>
                                <CreateIcon />
                            </IconButton>
                        </dev>
                    </dev>
                </section>
            </div>
            <hr className='pb-1 pt-1'/>
      </div>
    );
};

export default ItemCard;
