let callbackLists = {}
let eventHub = {
    trigger(eventName, data) {
        console.log('trigger', eventName)
        let callbackList = callbackLists[eventName]
        console.log(callbackList.length, callbackList)
        if (!callbackList) {
            return
        }
        for (let i = 0; i < callbackList.length; i++) {
            console.log('calling', i,  callbackList[i])
            callbackList[i](data)
        }
    },
    on(eventName, callback) {
        console.log('on', eventName, callback)
        if (!callbackLists[eventName]) {
            callbackLists[eventName] = []
        }
        callbackLists[eventName].push(callback)
        console.log('callbacklistslength', callbackLists.length)
        console.log('callbacklists(eventname).length', callbackLists[eventName].length)
    }
}

export default eventHub;
