import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import { useState, useEffect } from 'react';
import { init } from '../../backend/models/item';


const Sidebar = () => {
    const [values, setValues] = useState({
        name: '',
        amount: 1,
        note: '',
        urgent: false,
        error: false,
        success: false,
        loading: false
    });

