    return (
        <div className='sidebar'>
            <div className='sidebar__title'>
                <h2>Add Item</h2>
                <hr />
            </div>
            <form className='sidebar__form'>
                <div className='sidebar__input'>
                    <label>
                        <span>Name:</span>
                        <input type='text' className='ml-3'/>
                    </label>
                </div>
                <div className='sidebar__input'>
                    <label>
                        <span>Amount:</span>
                        <input type='number' min='1' className='ml-3'/>
                    </label>
                </div>
                <div className='sidebar__input'>
                    <label>
                        <span>Note:</span>
                        <input type='text' className='ml-3'/>
                    </label>
                </div>
                <div className='sidebar__input'>
                    <label>
                        <span>Urgent:</span>
                        <input type='checkbox' className='ml-3'/>
                    </label>
                </div>
                <div className='sidebar__input'>
                    <IconButton>
                        <AddShoppingCartIcon />
                    </IconButton>
                </div>
            </form>
        </div>
    );
};

export default Sidebar;
