import Link from 'next/link';
import renderHTML from 'react-render-html';
import moment from 'moment';
import { API } from '../../config'; 

const ItemCard = ({ item }) => {
    
    return (
        <div className='lead pb-2'>
            <header>
                <h2 className='pt-3 pb-1 font-weight-bold'>{item.name}</h2>
            </header>
            <section>
                <p className='mark ml-1 pt-2 pb-2'>
                    Written by {item.postedBy.username} | Added {moment(item.createdAt).fromNow()}
                </p>
            </section>
            <section>
                <p>Amount: {item.amount}</p>
            </section>
            <section>
                {item.urgent === 1 & (
                    <p>Urgent</p>
                )}
            </section>
            <div className='row pb-5'>
                <div className='col-md-8'>
                    <section>
                        <div className='pb-3'>{(item.note)}</div> 
                    </section>
                </div>
            </div>
            <hr className='pb-3 pt-3'/>
      </div>
    );
};

export default ItemCard;
