import Link from 'next/link';
import renderHTML from 'react-render-html';
import { useState, useEffect } from 'react';
import { listSearch } from '../../actions/item';
import { getCookie } from '../../actions/auth';
import SearchIcon from '@material-ui/icons/Search';
import { IconButton } from '@material-ui/core';

const Search = ({ updateParent }) => {
    const [token, setToken] = useState('');
    const [values, setValues] = useState({
        search: undefined,
        results: [],
        searched: false,
        message: ''
    });

    const { search, results, searched, message } = values;

    useEffect(() => {
        init();
    }, []);

    const init = () => {
        setToken(getCookie('token'));
    };

    const searchSubmit = e => {
        e.preventDefault();
        listSearch({ search }, token).then(data => {
            if (data.length === 1){
                setValues({ ...values, results: data, searched: true, message: `${data.length} item found` });
            } else {
                setValues({ ...values, results: data, searched: true, message: `${data.length} items found` });
            }
            
            updateParent(results, true);
        });
    };

    const handleChange = e => {
        setValues({ ...values, search: e.target.value, searched: false, results: [] });
        if (!e.target.value) {
            updateParent(results, false);
        }
    };

    const searchedItems = () => {
        return (
            <div className="jumbotron bg-white">
                {message && <p className="pt-4 text-muted font-italic">{message}</p>}
            </div>
        );
    };

    const searchForm = () => (
        <form className=''>
            <div className='row main__searchBar'>
                <div onClick={searchSubmit}>
                    <IconButton>
                        <SearchIcon />
                    </IconButton>
                </div>
                <div>
                    <input type="search" className="form-control" placeholder="Search items" onChange={handleChange} />
                </div>
            </div>
        </form>
    );

    return (
        <div className="container-fluid">
            <div className="pt-3 pb-5">{searchForm()}</div>
            {searched && <div>{searchedItems}</div>}
        </div>
    );
};

export default Search;
