import Link from 'next/link';
import renderHTML from 'react-render-html';
import moment from 'moment';
import { API } from '../../config'; 

const ItemCard = ({ item }) => {
    
    return (
        <div className='lead pb-2 pl-2'>
            <header>
                <h2 className='pt-1 pb-1 font-weight-bold'>{item.name}</h2>
            </header>
            <section>
                <p className='mark ml-1 pt-2 pb-2'>
                    Added by {/*item.postedBy.username*/} | Added {moment(item.createdAt).fromNow()}
                </p>
            </section>
            <section>
                <p>Amount: {item.amount}</p>
            </section>
            <section>
                {item.urgent === 1 && (
                    <p>Urgent</p>
                )}
            </section>
            <section>
                {item.note && (
                    <div className='pt-2'>
                        <p>
                            Note: {(item.note)}
                        </p>
                    </div>
                )}
            </section>
            <hr className='pb-1 pt-1'/>
      </div>
    );
};

export default ItemCard;
