import Link from 'next/link';
import renderHTML from 'react-render-html';
import moment from 'moment';
import { API } from '../../config'; 
import { updateItem, removeItem } from '../../actions/item';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import CreateIcon from '@material-ui/icons/Create';
import DoneIcon from '@material-ui/icons/Done';
import { FormLabel, Checkbox, IconButton} from '@material-ui/core';
import { useEffect, useState } from 'react';
import { getCookie } from '../../actions/auth';

const ItemCard = ({ item }) => {
    const [token, setToken] = useState('');
    const [values, setValues] = useState({
        name: '',
        amount: 0,
        urgent: 0,
        note: '',
        error: false,
        success: false,
        loading: false,
    });

    const { name, amount, urgent, note, error, success, loading } = values;

    const init = () => {
        setToken(getCookie('token'));
        setValues({
            ...values,
            name: item.name,
            amount: item.amount,
            urgent: item.urgent,
            note: item.note,
        });
    };

    useEffect(() => {
        init();
    }, []);

    const plusAmount = () => {
        setValues({
            ...values,
            amount: amount + 1
        });
        updateItem({
            slug: item.slug,
            amount: amount + 1
        }, token);
    };

    const minusAmount = () => {
        if (amount === 1) {
            removeItem({
                slug: item.slug
            }, token);
        } else {
            setValues({
                ...values,
                amount: amount - 1
            });
            updateItem({
                slug: item.slug,
                amount: amount - 1
            }, token);
        } 
    };

    const toggleUrgent = () => { 
        console.log(urgent);
        if (urgent === 0) {
            setValues({
                ...values,
                urgent: 1
            });
            updateItem({
                slug: item.slug,
                urgent: 1
            }, token);
        } else if (urgent === 1) {
            setValues({
                ...values,
                urgent: 0
            });
            updateItem({
                slug: item.slug,
                urgent: 0
            }, token);
        }
    };

    const updateNote = () => { // bug - can't delete note
        let newNote = prompt('Update Note:', note);
        setValues({
            ...values,
            note: newNote
        });
        updateItem({
            slug: item.slug,
            note: newNote
        }, token);
    };

    
    return (
        <div className='lead pb-2 main__card'>
            <header className='main__card__title'>
                <h3 className='pt-1 pl-1 pb-2 mb-0 font-weight-bold'>{name}</h3>
            </header>
            <div className='main__card__info pl-1'>
                <section>
                    <div className='row'>
                        <div>
                            <p className='pt-2 mb-0'>
                                Added by {item.postedBy} {moment(item.createdAt).fromNow()}
                            </p>
                        </div>
                        <div>
                            
                        </div>
                    </div>
                </section>
                <section>
                    <div className='row pl-3'> 
                        <div style={{display: 'grid', placeItems: 'center'}}>
                            <p className='mb-0 pr-2'>Amount: {amount}</p>
                        </div>
                       <div onClick={plusAmount}>
                            <IconButton>
                                <AddIcon />
                            </IconButton>
                       </div>
                       <div onClick={minusAmount}>
                            <IconButton >
                                <RemoveIcon />
                            </IconButton>
                       </div>
                    </div>
                </section>
                <section>
                    {urgent === 1 && (
                        <div>
                            <label>
                                <input type='checkbox' defaultChecked onChange={toggleUrgent}/>
                                <span>Urgent</span>
                            </label>
                        </div>
                    )}
                    {urgent === 0 && (
                        <div>
                            <label>
                                <input type='checkbox' onChange={toggleUrgent}/>
                                <span>Urgent</span>
                            </label>
                        </div>
                    )}
                </section>
                <section>
                    <div className='row pl-3'>
                        <div>
                            <div className='pt-2'>
                                <p>
                                    Note: {note}
                                </p>
                            </div>
                        </div>
                        <div>
                            <div className='pl-2' onClick={updateNote}>
                                <IconButton>
                                    <CreateIcon />
                                </IconButton>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
            <hr className='mb-0 mt-2'/>
      </div>
    );
};

export default ItemCard;
