import { FormLabel, Checkbox, IconButton} from '@material-ui/core';
import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import { useState, useEffect } from 'react';
import { init } from '../../backend/models/item';
import { createItem } from '../actions/item';
import { getCookie } from '../actions/auth';


const Sidebar = () => {
    const [token, setToken] = useState('');
    const [values, setValues] = useState({
        name: '',
        amount: 1,
        note: '',
        urgent: false,
        error: false,
        success: false,
        loading: false
    });

    const { name, amount, note, urgent, error, success, loading } = values;

    useEffect(() => {
        init();
    })

    const init = () => {
        setToken(getCookie('token'));
    }

    const handleChange = name => e => {
        let value;
        console.log(e.target.value);
        value = e.target.value;
        if(name === 'urgent') {
            value = document.getElementById("urgent-check").checked;
            console.log(value)
        }
        setValues({ ...values, [name]: value, error: false, success: false, loading: false })
    };

    const handleSubmit = () => {
        setValues({ ...values, loading: true });
        createItem({
            name: name,
            amount: amount,
            urgent: urgent,
            note: note
        }, token).then(data => {
            if(data.error) {
                console.log('data.error', data.error);
                setValues({ ...values, error: data.error, loading: false });
            } else {
                setValues({
                    ...values,
                    name: '',
                    amount: 1,
                    urgent: false,
                    note: '',
                    error: false,
                    success: true,
                    loading: false
                });
            }
        });
    };

    const showError = () => (
        <div className='alert alert-danger mt-2' style={{ display: error ? '' : 'none' }}>
            {error}
        </div>
    );

    const showSuccess = () => (
        <div className='alert alert-success mt-2' style={{ display: success ? '' : 'none' }}>
            Item added
        </div>
    );

    const showLoading = () => (
        <div className='alert alert-info mt-2' style={{ display: loading ? '' : 'none' }}>
            Loading...
        </div>
    );

    return (
        <div className='sidebar'>
            <div className='sidebar__title'>
                <h2>Add Item</h2>
                <hr />
            </div>
            <form className='sidebar__form'>
                <div className='sidebar__input'>
                    <label>
                        <span>Name:</span>
                        <input onChange={handleChange('name')} type='text' className='sidebar__input__field'/>
                    </label>
                </div>
                <div className='sidebar__input'>
                    <label>
                        <span>Amount:</span>
                        <input onChange={handleChange('amount')} type='number' min='1' className='sidebar__input__field'/>
                    </label>
                </div>
                <div className='sidebar__input'>
                    <label>
                        <span>Note:</span>
                        <input onChange={handleChange('note')} type='text' className='sidebar__input__field'/>
                    </label>
                </div>
                <div className='sidebar__input'>
                    <label>
                        <input id='urgent-check' type='checkbox' onChange={handleChange('urgent')} className='sidebar__input__field'/>
                        <span>Urgent</span>
                    </label>
                </div>
                <div className='sidebar__input' onClick={handleSubmit}>
                    <IconButton>
                        <AddShoppingCartIcon />
                    </IconButton>
                </div>
                <div>
                    {showSuccess()}
                    {showError()}
                    {showLoading()}
                </div>
            </form>
        </div>
    );
};

export default Sidebar;
