import { FormLabel, Checkbox, IconButton} from '@material-ui/core';
import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import { useState, useEffect } from 'react';
import { init } from '../../backend/models/item';
import { createItem } from '../actions/item';


const Sidebar = () => {
    const [token, setToken] = useState('');
    const [values, setValues] = useState({
        name: '',
        amount: 1,
        note: '',
        urgent: false,
        error: false,
        success: false,
        loading: false
    });

    const { name, amount, note, urgent, error, success, loading } = values;

    useEffect(() => {
        init();
    })

    const init = () => {
        setToken(getCookie('token'));
    }

    const handleChange = name => e => {
        const value = e.target.value;
        setValues({ ...values, [name]: value, error: false, success: false, loading: false })
    };

    const handleSubmit = () => {
        e.preventDefault();

        setValues({ ...values, loading: true });
        createItem({
            name: name,
            amount: amount,
            urgent: urgent,
            note: note
        }, token);
    };

    return (
        <div className='sidebar'>
            <div className='sidebar__title'>
                <h2>Add Item</h2>
                <hr />
            </div>
            <form className='sidebar__form'>
                <div className='sidebar__input'>
                    <label>
                        <span>Name:</span>
                        <input type='text' className='ml-3'/>
                    </label>
                </div>
                <div className='sidebar__input'>
                    <label>
                        <span>Amount:</span>
                        <input type='number' min='1' className='ml-3'/>
                    </label>
                </div>
                <div className='sidebar__input'>
                    <label>
                        <span>Note:</span>
                        <input type='text' className='ml-3'/>
                    </label>
                </div>
                <div className='sidebar__input'>
                    <label>
                        <span>Urgent:</span>
                        <input type='checkbox' className='ml-3'/>
                    </label>
                </div>
                <div className='sidebar__input'>
                    <IconButton>
                        <AddShoppingCartIcon />
                    </IconButton>
                </div>
            </form>
        </div>
    );
};

export default Sidebar;
