import { getItems } from '../actions/item';
import ItemCard from '../components/item/ItemCard';
import { getCookie, isAuth } from '../actions/auth';
import { useState, useEffect } from 'react';
import cookie from 'js-cookie';

const Main = () => {

    const [items, setItems] = useState([]);
    const [token, setToken] = useState('');

    useEffect(() => {
        initItems();
    }, []);

    useEffect(() => {
        initToken();
    }, '');

    const initToken = () => {
        setToken(isAuth());
    };

    const initItems = () => {
        const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZjU4ZGYwZjU0ZDgyZTMyMWM5ZjdhMjAiLCJpYXQiOjE1OTk3NjA4NDcsImV4cCI6MTU5OTg0NzI0N30.4g7x1JfhoBSp37JTr8h90tqmMXFKPufZIRXCgQ0T5_8';
        getItems(token).then(data => {
            if(data.error) {
                console.log(data.error);
            } else {
                setItems(data);
            }
        });
    };


    const showListedItems = () => {
        console.log(getCookie('token'));
        return items.map((item, i) => {
            return (
                <article key={i}>
                    <ItemCard item={item}/>
                </article>
            );
        });
    };
  

    return (
        <div className='main'>
            <div className='main__title'>
                <h2>Shopping List</h2>
                <hr />
            </div>
            <div className='main__body'>
                {showListedItems()}
            </div>
        </div>
    );
};


export default Main;
